const express = require('express');
const axios = require('axios');

const app = express();
const port = 3000;


app.get('/java', async (req, res) => {
  try {
    const response = await axios.get('direccion');
    res.send(`Respuesta del servidor Java: ${response.data}`);
  } catch (error) {
    res.status(500).send('Error al conectarse al servidor Java');
  }
});

app.get('/python', async (req, res) => {
  try {
    const response = await axios.get('http://direccion-del-servidor-python');
    res.send(`Respuesta del servidor Python: ${response.data}`);
  } catch (error) {
    res.status(500).send('Error al conectarse al servidor Python');
  }
});

app.listen(port, () => {
  console.log(`Servidor Node.js en ejecuci√≥n en el puerto ${port}`);
});
